sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   // Today's date
   var today = new Date();
   today.setHours(0, 0, 0, 0);  // normalize to midnight

   // Get selected date from form
   var selectedDateStr = g_form.getValue('u_today_date'); // format: yyyy-MM-dd
   var selectedDate = new Date(selectedDateStr);
   selectedDate.setHours(0, 0, 0, 0);

   if (selectedDate >= today) {
      g_form.clearMessages();
   } else {
      g_form.addErrorMessage("Please select today (" + today.toLocaleDateString() + ") or a future date");
      g_form.setValue('u_today_date', '');   // fixed field name
   }
}
