sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
   var seven=g_form.getValue('u_7');
   if(seven=="true"){
	g_form.setDisplay('u_1',false);
	g_form.setDisplay('u_3',false);
	g_form.setDisplay('u_4',false);
	g_form.setDisplay('u_5',false);
	g_form.setDisplay('u_6',false);
	g_form.setDisplay('u_2',false);
	g_form.setDisplay('u_8',false);
	g_form.setDisplay('u_9',false);
	g_form.setDisplay('u_10',false);
	g_form.setReadOnly('booking_day',true);
	g_form.addInfoMessage("Thank you for choosing this slot,we allow only one slot for one day");
   }
   else{
	g_form.setDisplay('u_1',true);
	g_form.setDisplay('u_3',true);
	g_form.setDisplay('u_4',true);
	g_form.setDisplay('u_5',true);
	g_form.setDisplay('u_6',true);
	g_form.setDisplay('u_2',true);
	g_form.setDisplay('u_8',true);
	g_form.setDisplay('u_9',true);
	g_form.setDisplay('u_10',true);
	g_form.setReadOnly('booking_day',true);
	g_form.clearMessages();
   }

}
